hudson.tasks.Shell.command:
# copy certificate into keystore TOMCAT_KS_PASS=${TOMCAT_KS_PASS:-changeit} TOMCAT_KS_FILE=${TOMCAT_KS_FILE:-/etc/grid-security/tomcat-keystore}
sudo openssl pkcs12 -export -in /etc/grid-security/hostcert.pem -inkey /etc/grid-security/hostkey.pem \ -out /etc/grid-security/hostcert.p12 -name tomcat -CApath /etc/grid-security/certificates \ -caname root -chain -passout pass:${TOMCAT_KS_PASS}
sudo keytool -importkeystore -noprompt -srckeystore /etc/grid-security/hostcert.p12 -srcstorepass ${TOMCAT_KS_PASS} \ -destkeystore ${TOMCAT_KS_FILE} -srcstoretype pkcs12 -storepass ${TOMCAT_KS_PASS}
sudo chmod 600 /etc/grid-security/hostcert.p12 ${TOMCAT_KS_FILE} sudo chown tomcat:tomcat ${TOMCAT_KS_FILE}
# configure ssl in tomcat if [ -z `xmlstarlet sel -T -t -i "/Server/Service/Connector[@port='8443']" -o exists ${CATALINA_HOME}/conf/server.xml` ]; then
sudo xmlstarlet ed -L -s /Server/Service -t elem -n SSLConnector -v "" \ -i //SSLConnector -t attr -n "port" -v "443" \ -i //SSLConnector -t attr -n "protocol" -v "org.apache.coyote.http11.Http11Protocol" \ -i //SSLConnector -t attr -n "SSLEnabled" -v "true" \ -i //SSLConnector -t attr -n "maxThreads" -v "150" \ -i //SSLConnector -t attr -n "scheme" -v "https" \ -i //SSLConnector -t attr -n "secure" -v "true" \ -i //SSLConnector -t attr -n "clientAuth" -v "false" \ -i //SSLConnector -t attr -n "sslProtocol" -v "TLS" \ -i //SSLConnector -t attr -n "keystoreFile" -v "${TOMCAT_KS_FILE}" \ -i //SSLConnector -t attr -n "keystorePass" -v "${TOMCAT_KS_PASS}" \ -r //SSLConnector -v Connector \ ${CATALINA_HOME}/conf/server.xml fi
# change port 8080 to 80 and 8443 to 443 sudo sed -i 's/port="8080"/port="80"/g' ${CATALINA_HOME}/conf/server.xml sudo sed -i 's/redirectPort="8443"/redirectPort="443"/g' ${CATALINA_HOME}/conf/server.xml
